import React, { Component } from "react";


function Day(props) {
    return (
        <div className="card">
            <img src="data:image/png;base64,"
                 className="card-img-top" alt="Cloud" />
            <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">{props.day.id}</p>
                <p className="card-text">{props.day.weather_state_name}</p>
                <p className="card-text">{props.day.weather_state_abbr}</p>
                <p className="card-text">{props.day.wind_direction_compass}</p>
                <p className="card-text">{props.day.created}</p>
                <p className="card-text">{props.day.applicable_date}</p>

            </div>
        </div>
    )
}

function createImage(name) {
    switch(name) {
        case "Showers" :
        case "Light Rain" : return "https://ssl.gstatic.com/onebox/weather/48/rain_light.png";
        case "Light Cloud" :
        case "Heavy Cloud" : return "https://ssl.gstatic.com/onebox/weather/48/rain_s_cloudy.png"
        default:
            return ""
    }
}

class DashBoard extends Component {

    state = {
        days: [],
        inputLinkClicked: false,
        index : 0
    }

    componentDidMount() {

        const cors_api_url = 'https://cors-anywhere.herokuapp.com/';
        fetch(cors_api_url + "https://www.metaweather.com/api/location/924938/", {
                method: 'GET'
            })
            .then(result => result.json())
            .then(data => {
                document.getElementById("loader").remove();
                this.setState({
                    days : data.consolidated_weather,
                    inputLinkClicked: true
                });
            })
            .catch((error) => console.log(error));
    }

    render() {
        return (
            <div className="my_container" id="container">
                <div className="loader" id="loader"></div>
                {
                    this.state.inputLinkClicked ?
                        this.state.days.map(day => {
                            return (
                                <Day day={day} key={day.ideal} />
                            )
                        })
                        :
                        <div></div>
                }
            </div>
        );
    }
}

export default DashBoard;
