import React, { Component } from "react";

class RegisterForm extends Component {
    constructor(props) {
        super(props);

        this.state = {name: '', password: ""};

        this.name_change = this.name_change.bind(this);
        this.password_change = this.password_change.bind(this);

        this.handle_submit = this.handle_submit.bind(this);
    }

    name_change(event) {
        this.setState({name: event.target.value});
    }

    password_change(event) {
        this.setState({password: event.target.value});
    }

    // handleSubmit(event) {
    //     let str = this.state.name.substring(0, this.state.name.length - 1);
    //     if (+this.state.name.slice(-1) != NaN && /^[a-zA-Z][a-zA-Z\s]*$/.test(str)) {
    //
    //     }
    //     event.preventDefault();
    // }
    handle_submit = event => {
        event.preventDefault();

        let str = this.state.name.substring(0, this.state.name.length - 1);
        if (parseInt(this.state.name.slice(-1)) != NaN && /^[a-zA-Z][a-zA-Z\s]*$/.test(this.state.name.substring(0, this.state.name.length - 1))) {
            this.props.history.push("/");
        }
    }


    render() {
        return (
            <form onSubmit={this.handle_submit}>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Name</label>
                    <input value={this.state.name} onChange={this.name_change} type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Password</label>
                    <input value={this.state.password} onChange={this.password_change} type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        );
    }
}

export default RegisterForm;
