import React, { Component } from "react";

class DashBoard extends Component {

    state = {

    }

    componentDidMount() {


        const cors_api_url = 'https://cors-anywhere.herokuapp.com/';
        fetch(cors_api_url + "https://www.metaweather.com/api/location/924938/", {
            method: 'GET',

            })
            .then(result => result.json())
            .then(data => {
                document.getElementById("loader").remove();
                document.getElementById("container").append(renderDays(data.consolidated_weather));
            })
            .catch((error) => console.log(error));
    }



    render() {

        return (
            <div className="my_container" id="container">
                <div className="loader" id="loader"></div>
            </div>
        );
    }
}


function Day(props) {
    return (
        <div className="card">
            <img src="..." className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">Card title</h5>
                    <p className="card-text">${props.day.id}</p>

                </div>
        </div>
    )

}

function renderDays(days) {
    return days.map(day => {
        return (
            <Day day={day} key={day.id}/>
        )
    })
}



export default DashBoard;
