import React, { Component } from "react";

class DashBoard extends Component {
    componentDidMount() {
        var cors_api_url = 'https://cors-anywhere.herokuapp.com/';
        function doCORSRequest(options, printResult) {
            var x = new XMLHttpRequest();
            x.open(options.method, cors_api_url + options.url);
            x.onload = x.onerror = function() {
                printResult(
                    options.method + ' ' + options.url + '\n' +
                    x.status + ' ' + x.statusText + '\n\n' +
                    (x.responseText || '')
                );
            };
            if (/^POST/i.test(options.method)) {
                x.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
            }
            x.send(options.data);
        }

        // Bind event
        (function() {
            var urlField = document.getElementById('url');
            var dataField = document.getElementById('data');
            var outputField = document.getElementById('output');
            document.getElementById('get').onclick =
                document.getElementById('post').onclick = function(e) {
                    e.preventDefault();
                    doCORSRequest({
                        method: 'GET',
                        url: "https://www.metaweather.com/api/location/924938/"
                    }, function printResult(result) {
                        outputField.value = result;
                    });
                };
        })();
        if (typeof console === 'object') {
            console.log('// To test a local CORS Anywhere server, set cors_api_url. For example:');
            console.log('cors_api_url = "http://localhost:8080/"');
        }

        fetch('/users/addCourse', {
            method: 'POST',
            headers: {
                'Accept' : 'application/json',
                'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                courseId: courseId,
                courseName: courseName
            })
        })
            .then(() => { return fetch(`/courses/graph/${courseId}`).then(x => x.json()) })
            .then((response) => CreateGraph(response.nodes, response.root, response.user ,response.teacher))
            .catch((error) => console.log(error));
    }

    render() {
        return (
            <div>
                <h2>HELLO</h2>

            </div>
        );
    }
}

export default DashBoard;
