import React, { Component } from "react";

class DashBoard extends Component {
    componentDidMount() {


        const cors_api_url = 'https://cors-anywhere.herokuapp.com/';
        fetch(cors_api_url + "https://www.metaweather.com/api/location/924938/", {
            method: 'GET',

            })
            .then(result => result.json())
            .then(data => {
                alert(data.consolidated_weather);

            })
            .catch((error) => console.log(error));
    }



    render() {

        return (
            <div className="my_container">
                <div className="loader"></div>
            </div>
        );
    }
}

class HealthConditions extends React.Component {
    constructor(props) {
        super(props);


        this.state = {
            inputLinkClicked: false
        }
    }

    handleAddSecondInput() {
        this.setState({
            inputLinkClicked: true
        })
    }


    render() {
        return(
            <main id="wrapper" className="" data-reset-cookie-tab>
                <div id="content" role="main">
                    <div className="inner-block">

                        <h1 title="Tell us about any disabilities, illnesses or ongoing conditions"/>

                        <input label="Name of condition"
                                    InputType="text"
                                    InputId="id-condition"
                                    InputName="condition"
                        />

                        {
                            this.state.inputLinkClicked?

                                <input label=""
                                            InputType="text"
                                            InputId="id-condition2"
                                            InputName="condition2"
                                />

                                :

                                <div></div>
                        }

                        <button
                            type="button"
                            className="make-button-link"
                            data-add-button=""
                            href="#"
                            onClick={this.handleAddSecondInput}
                        >
                            Add a condition
                        </button>

                        <FormButton buttonLabel="Next"
                                    handleSubmit={this.handleSubmit}
                                    linkto={
                                        this.state.illnessOrDisability === 'true' ?
                                            "/404"
                                            :
                                            "/add-your-details"
                                    }
                        />


                    </div>
                </div>
            </main>
        );
    }
}
